import React from 'react'
import logo from './logo.svg';
import './App.css';
import { render } from 'react-dom';

class App extends React.Component{

  constructor(props) {
    super(props)
    this.state = {
      pokemon: null,
      index: null
    }
  }

  async componentDidMount() {
    const response = await fetch('http://localhost:4242/pokemons')
    const data = await response.json()
    this.setState({pokemon: data.[5][0].nom,
      index: data.[5][0].numéro
    })
  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            heelo to pokedex
          </p>
          <span> JSON : {this.state.pokemon || 'no data' } </span>
          <img src={`https://assets.pokemon.com/assets/cms2/img/pokedex/detail/${this.state.index || 'no data' }.png`}></img>
        </header>
      </div>
    );
  }
}
export default App;
