import React from 'react'
import { Jumbotron } from 'react-bootstrap';
import { Table } from 'react-bootstrap';
import { Container } from 'react-bootstrap';
import { Row } from 'react-bootstrap';
import { Col } from 'react-bootstrap';

class App extends React.Component{

  constructor(props) {
    super(props)
    this.state = {
      pokemon: null,
      index: null,
      nomen: null,
      nomja: null,
      espece: null,
      taille: null,
      numéro: null,
      nomde: null,
      nomtm: null,
      type1: null,
      type2: null,
      forme: null,
      poids: null,
      couleur: null,

    }
  }

  async componentDidMount() {
    const response = await fetch('http://localhost:4242/pokemons/004')
    const data = await response.json()
    this.setState({
      pokemon: data.nom,
      numéro: data.numéro,
      nomen: data.nomen,
      nom: data.nom,
      nomja: data.nomja,
      espece: data.espece,
      taille: data.taille,
      type1: data.type1,
      type2: data.type2,
      forme: data.forme,
      poids: data.poids,
      nomde: data.nomde,
      nomtm: data.nomtm,
      type1: data.type1,
      type2: data.type2,
      forme: data.forme,
      couleur: data.couleur,
    })
    //console.log(data)
  }
 render(){
    return (
      <div className="App">
        <header className="App-header">
        <p>
          Welcome into POKEDEX
        </p>
        <div> {data} </div>


        </header>
      </div>

    );
  }
}
export default App;
